mapper.insert:
insert into Export_TOP (tradingVolume, minimumPrice, middlePrice, 
  highestPrice, varietyName, marketName
  )
values (#{tradingvolume,jdbcType=DOUBLE}, #{minimumprice,jdbcType=DOUBLE}, #{middleprice,jdbcType=DOUBLE}, 
  #{highestprice,jdbcType=DOUBLE}, #{varietyname,jdbcType=LONGVARCHAR}, #{marketname,jdbcType=LONGVARCHAR}
  )

mapper.select:
select tradingVolume, minimumPrice, middlePrice, highestPrice, varietyName, marketName from Export_TOP
varietyName =#{name}
order by tradingVolume desc limit 5
